package com.example.evc3_faichin_diaz_yessica.fragments;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.evc3_faichin_diaz_yessica.R;
import com.example.evc3_faichin_diaz_yessica.databinding.FragmentPlatillosBinding;
import com.example.evc3_faichin_diaz_yessica.model.Platillos;

import java.util.ArrayList;
import java.util.List;


public class platillosFragment extends Fragment {

    private FragmentPlatillosBinding binding;

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentPlatillosBinding.inflate(inflater,container,false);
        return binding.getRoot();
    }

    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        AdapterPlatillo adapter = new AdapterPlatillo(getData());
        binding.rvPlatillos.setAdapter(adapter);
        LinearLayoutManager layoutManager =  new LinearLayoutManager(view.getContext(),LinearLayoutManager.HORIZONTAL,  false);
        binding.rvPlatillos.setLayoutManager(layoutManager);

    }
    private List<Platillos> getData() {
        List<Platillos> platillos = new ArrayList<>();

        platillos.add(new Platillos("Arepas", "Deliciosos panes de maíz rellenos de diferentes ingredientes como queso, carne, pollo, aguacate o perico.", "https://ichef.bbci.co.uk/news/640/cpsprodpb/1400C/production/_93723918_thinkstockphotos-627042504.jpg"));
        platillos.add(new Platillos("Pabellón Criollo", "El plato nacional, compuesto por arroz, carne desmechada, caraotas (frijoles negros) y tajadas de plátano maduro.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Cachapa", "Una especie de crepe hecha de maíz tierno, rellena con queso y servida con mantequilla.", "https://i.ytimg.com/vi/4W4z6eFOMwQ/maxresdefault.jpg"));
        platillos.add(new Platillos("Hallaca", "Un tamal venezolano relleno de un guiso de carne de res, cerdo, pollo, alcaparras, aceitunas, pasas y envuelto en hojas de plátano.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Cachito", "Un pan relleno de jamón, queso y a veces también con tocineta", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Asado Negro", "Carne de res cocida a fuego lento en una salsa dulce a base de vino y papelón (panela).", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Chupe Andino", "Sopa espesa preparada con queso andino, leche, mantequilla y papas.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Tequeños", " Palitos de queso envueltos en masa y fritos, servidos como aperitivo.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Mandocas", " Bolas fritas hechas de masa de plátano, queso rallado y papelón", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Cazabe", " Pan plano hecho de yuca, tradicional de la etnia indígena Warao.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Dulce de Lechosa", "Postre de papaya verde cocida en almíbar de papelón y especias.", "https://i.blogs.es/f453c3/dulce_lechosa/450_1000.jpg"));
        platillos.add(new Platillos("Pasticho", "La versión venezolana de la lasaña, hecha con capas de pasta, carne y bechamel.", "data:image/jpeg;base64,"));
        platillos.add(new Platillos("Tizana", "Refrescante bebida a base de jugo de frutas frescas mezclado con trozos de frutas y hielo.", "data:image/jpeg;base64,"));
        return platillos;
    }
}